 pc45 = new Part("curve45_240", [Bezier.parsePath("M -240 0 C -240 -63.6 -214.68 -124.73 -169.71 -169.71")]);

 psplitrsc45 = new Part("split-right-s-c45-240", [Bezier.parsePath("M 0 0 C 0 -80 0 -160 0 -240"), Bezier.parsePath("M 0 0 C 0 -63.6 25.32 -124.73 70.27 -169.71")]);
